import re; content = open("deployment/health_check_prod.py", "r", encoding="utf-8").read(); content = re.sub(r"(def check_http_endpoints\(self\) -> bool:\s*\n\s*\"\"\"Check HTTP endpoints\"\"\"\s*\n\s*self\.log_info\(\"HTTP 엔드포인트 확인 중\.\.\.\"\))", r"\1\n        \n        # Skip HTTP endpoint checks in GitHub Actions or Windows\n        if self.is_github_actions or platform.system() == \"Windows\":\n            self.log_info(\"GitHub Actions/Windows 환경에서는 HTTP 엔드포인트 확인을 건너뜁니다\")\n            self.health_results.append(\"HTTP Endpoints: SKIPPED (GitHub Actions/Windows)\")\n            return True", content, flags=re.MULTILINE); content = re.sub(r"(def check_database_connectivity\(self\) -> bool:\s*\n\s*\"\"\"Check database connectivity through application\"\"\"\s*\n\s*self\.log_info\(\"데이터베이스 연결 확인 중\.\.\.\"\))", r"\1\n        \n        # Skip database checks in GitHub Actions or Windows\n        if self.is_github_actions or platform.system() == \"Windows\":\n            self.log_info(\"GitHub Actions/Windows 환경에서는 데이터베이스 연결 확인을 건너뜁니다\")\n            self.health_results.append(\"Database: SKIPPED (GitHub Actions/Windows)\")\n            return True", content, flags=re.MULTILINE); open("deployment/health_check_prod.py", "w", encoding="utf-8").write(content); print("Health check script modified successfully")
